<script lang="ts">
	import { STD } from "$lib";
	import QuizPage from "@/src/lib/QuizPage.svelte";

	let index = $state(0);
	let correct_count = $state(0);

	let questions = new STD.NestedQuestion(
		{ Quote: "İnterfazda 4 kromozomu olan hücrenin anafazda kaç kromozomu vardır?", CorrectAnswer: "C", Options: { A: "2", B: "4", C: "8", D: "16" }, Image: "data:image/jpeg;base64," },
		{ Quote: "1+2", CorrectAnswer: "C", Options: { A: "1", B: "2", C: "3", D: "4" } },
		{ Quote: "1+3", CorrectAnswer: "D", Options: { A: "1", B: "2", C: "3", D: "4" } },
		{ Quote: "1+4", CorrectAnswer: "D", Options: { A: "2", B: "3", C: "4", D: "5" } },
		{ Quote: "1+5", CorrectAnswer: "B", Options: { A: "5", B: "6", C: "7", D: "8" } }
	);
</script>

<div class="center flex size-full">
	<main class="flex h-full w-full flex-col justify-center bg-black p-4">
		{#key index}
			{#if index < questions.toArray().length}
				<QuizPage
					Question={questions.at(index)!}
					onCorrect={() => {
						correct_count++;
					}}
					onFinish={() => {
						setTimeout(() => index++, 1000);
					}}
				/>
			{:else}
				{correct_count} / {questions.toArray().length}
			{/if}
		{/key}
	</main>
</div>
